import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getInfo } from '../store/reducer';
import CardAC from './CardAC';

const AdminContent = () => {
    const { infos } = useSelector(state => state.info);
    const dispatch = useDispatch();
    const [id, setId] = useState("")
    const [data,setData] = useState({
        title:'',
        description:'',
        myFile:'',
        categoryId:'',
    })
    console.log("admincontent",infos)

    useEffect(() =>{
        dispatch(getInfo());
        
      },[]) 
  return (
    <div>
        {infos?infos.map(i=>(<CardAC props={i}/>)):null}
        <form>
            {/* <select class="form-select form-select-sm" aria-label=".form-select-sm example">
                <option selected>selecciona el contenido a modificar</option>
                {infos?infos.map(i=>(<option value={i.id}>{i.title}</option>)):null}
            </select> */}
            
            <div class="col-md-4">
                <label for="validationCustom01" class="form-label">Titulo</label>
                <input type="text" class="form-control" id="validationCustom01"  required/>
                <div class="valid-feedback">
                Looks good!
                </div>
            </div>

            <div class="mb-3">
                <label for="exampleFormControlTextarea1" class="form-label">Descripcion</label>
                <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
            </div>

            <select class="form-select form-select-sm" aria-label=".form-select-sm example">
                <option selected>selecciona una categoria</option>
                <option value="1">entretenimiento</option>
                <option value="2">aventura</option>
                <option value="3">infantil</option>
                <option value="4">adultos</option>
            </select>
        
            <div class="mb-3">
                <label for="formFile" class="form-label">Default file input example</label>
                <input class="form-control" type="file" id="formFile"/>
            </div>
            
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    </div>
  )
}

export default AdminContent
