    const { infos } = useSelector(state => state.info);
    const dispatch = useDispatch();
    // const [data,setData] = useState([])
    console.log("admincontent",infos)

    useEffect(() =>{
        dispatch(getInfo());
        
      },[]) 
  return (
    <div>
      {infos?infos.map(i=>(<CardAC porps={i}/>)):null}
    </div>
  )
}

export default AdminContent
